<div>
  <div>Select one of 0 countries:</div>
  <select [formControl]="countryDropdown">
    <option *ngFor="let country of countriesData" value="">
      {{country.description}}
    </option>
  </select>
  <div>Current value: {{countryValue.value}}</div>
</div>
