import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormControl } from '@angular/forms';
import { Observable } from 'rxjs';
import { Country, CountryService } from './country.service';


@Component({
  selector: 'app-exercise1',
  templateUrl: './exercise1.component.html',
  styleUrls: ['./exercise1.component.css']
})
export class Exercise1Component implements OnInit {
  public countriesData:Observable<Country[]>;
  public countryDropdown = new FormControl<Country['id']>(null);

  constructor(
    private http:HttpClient,
    private countryService:CountryService
  ) { }

  ngOnInit() {
    this.countriesData = this.countryService.getCountryData()
    this.countryDropdown.valueChanges.subscribe(data =>{
      console.log(data);
    })
  }

}

<img src="assets/exercise1.gif" />

<h2>Result</h2>
<!-- Edit the code down below to implement the requested features -->
<div>
  <div>Select one of {{countriesData.length}} countries:</div>
  <select [formControl]="countryDropdown">
    <option *ngFor="let country of countriesData" [value]="country.id">
      {{country.description}}
    </option>
  </select>
  <div>Current value: {{countryDropdown.value}}</div>
</div>
