import { Component } from '@angular/core';
import {Observable} from 'rxjs';
import {Country} from './types';
import {CountryService} from './country.service';
import {FormControl} from '@angular/forms';

@Component({
  selector: 'app-exercise2',
  templateUrl: './exercise2.component.html',
  styleUrls: ['./exercise2.component.css']
})

export class Exercise2Component {

  countries$: Observable<Country[]> = this.service.getCountries();
  countryDropdown = new FormControl<Country['id']>(null);

  constructor(private service: CountryService) { }

}
<h2>Exercise 2</h2>
<h3>Your mission is to connect the two following dropdowns so that the state/province dropdown gets populated with data from the selected country.</h3>
<img src="assets/exercise2.gif" />
<br  style="width: ;">
<div *ngIf="countries$ | async as countries">
  <div>Select one of {{countries.length}} countries:</div>
  <select [formControl]="countryDropdown">
    <option *ngFor="let country of countries" [value]="country.id">
      {{country.description}}
    </option>
  </select>
  <div>Current value: {{countryDropdown.value}}</div>
</div>
<div>
  <div>Select one of 0 states/provinces:</div>
  <select #statesDropdown>
    <option>
      State or province
    </option>
  </select>
  <div>Current value: {{statesDropdown.value}}</div>
</div>
