import { Component, OnInit } from '@angular/core';
import { combineLatest, Observable, Subject } from 'rxjs';
import { Country, State } from './types';
import { FormControl } from '@angular/forms';
import { CountryService } from './country.service';
import { map, startWith, withLatestFrom } from 'rxjs/operators';

@Component({
  selector: 'app-exercise4',
  templateUrl: './exercise4.component.html',
  styleUrls: ['./exercise4.component.css']
})
export class Exercise4Component {

  countries$: Observable<Country[]>;
  states$!: Observable<State[]> = this.service.getStatesFor('');
  state!: State;
  countryControl = new FormControl<string>('');
  stateControl = new FormControl<string>({ value: '', disabled: true });
  countryId = new Subject();

  constructor(private service: CountryService) {
    this.countries$ = combineLatest([
      this.service.getCountries(),
      this.countryControl.valueChanges.pipe(
        startWith('')
      )
    ]).pipe(
      map(([countries, countryInput]) => countries.filter(c => c.description.toLowerCase().indexOf((countryInput ?? "").toLowerCase()) !== -1))
    );

    this.states$ = combineLatest([
      this.states$,
      this.stateControl.valueChanges.pipe(
        startWith('')
      ),
    ]).pipe(
      map(([states, stateInput]) => {
        return states.filter(state => state.description.toLowerCase().indexOf(stateInput.toLowerCase() ?? "") !== -1)
      })
    );
  }

  updateStates(country: Country) {
    this.countryControl.setValue(country.description);
    this.stateControl.enable();
    this.states$ = this.service.getStatesFor(country.id)
    this.stateControl.setValue('');
  }
}
<h2>Exercise 4</h2>
<div >
  <div class="dropdown">
    <input type="text" class="dropbtn" [formControl]="countryControl"
           placeholder="Enter country name" >
    <div class="dropdown-content" *ngIf="countries$ | async as countries">
      <span *ngFor="let country of countries" (click)="updateStates(country)"
            [innerHTML]="country.description | highlight: countryControl.value">
      </span>
    </div>
  </div>
</div>
<div>
  <div class="dropdown">
    <input type="text" class="dropbtn" [formControl]="stateControl" placeholder="Enter your state">
    <div class="dropdown-content" *ngIf="states$ | async as states">
      <span *ngFor="let s of states" (click)="state = s" [innerHTML]="s.description | titlecase | highlight: stateControl.value"></span>
    </div>
  </div>
</div>
