import { Component } from '@angular/core';
import {Observable} from 'rxjs';
import {Country, State} from './types';
import {CountryService} from './country.service';
import {FormControl} from '@angular/forms';

@Component({
  selector: 'app-exercise2',
  templateUrl: './exercise2.component.html',
  styleUrls: ['./exercise2.component.css']
})

export class Exercise2Component {

  countries$: Observable<Country[]> = this.service.getCountries();
  states$: Observable<State[]> = this.service.getStates();
  states = [];
  countryDropdown = new FormControl<Country['id']>(null);
  statesDropdown = new FormControl<State['id']>(null);


  constructor(private service: CountryService) { 
    this.countryDropdown.valueChanges.subscribe(data=>{
      this.states = this.states.filter(state => state.countryCode === data);
      console.log(data);
    });

    this.states$.subscribe(data =>{
      this.states = data;
    })
  }

}
