import { Component, OnInit } from '@angular/core';
import {Observable} from 'rxjs';
import {Country, State} from './types';
import {FormControl} from '@angular/forms';
import {CountryService} from './country.service';
import {map, withLatestFrom} from 'rxjs/operators';

@Component({
  selector: 'app-exercise4',
  templateUrl: './exercise4.component.html',
  styleUrls: ['./exercise4.component.css']
})
export class Exercise4Component {

  countries$: Observable<Country[]>;
  states$!: Observable<State[]>;
  state!: State;
  countryControl = new FormControl<string>('');
  stateControl = new FormControl<string>('');

  constructor(private service: CountryService) {
    this.countries$ = this.countryControl.valueChanges.pipe(
      withLatestFrom(this.service.getCountries()),
      map(([userInput, countries]) =>
        countries.filter(c => c.description.toLowerCase().indexOf((userInput ?? "").toLowerCase()) !== -1))
    );
  }

  updateStates(country: Country) {
    this.countryControl.setValue(country.description);
    this.states$ = this.stateControl.valueChanges.pipe(
      withLatestFrom(this.service.getStatesFor(country.id)),
      map(([userInput, states])=>{
        return states.filter(state => state.description.toLowerCase().includes(userInput.toLowerCase()))
      })
    )
  }
}
<h2>Exercise 4</h2>
<div >
  <div class="dropdown">
    <input type="text" class="dropbtn" [formControl]="countryControl"
           placeholder="Enter country name" >
    <div class="dropdown-content" *ngIf="countries$ | async as countries">
      <span *ngFor="let country of countries" (click)="updateStates(country)"
            [innerHTML]="country.description | highlight: countryControl.value">
      </span>
    </div>
  </div>
</div>
<div>
  <div class="dropdown">
    <input type="text" class="dropbtn" [formControl]="stateControl" [disabled]="states.length == 0" placeholder="Enter your state">
    <div class="dropdown-content" *ngIf="sate">
      <span *ngFor="let s of states" (click)="state = s" [innerHTML]="s.description | titlecase | highlight: stateControl.value"></span>
    </div>
  </div>
</div>
